item.description:
Add a Smarty Plugin folder with a Plugin It can be solved with a simple CompilerPass in your Plugin
{% code title=&ldquo;MyPlugin/Components/CompilerPass/AddTemplatePluginDirCompilerPass.php&rdquo; %}
&lt;?php namespace MyPlugin\Components\CompilerPass; use Symfony\Component\DependencyInjection\Compiler\CompilerPassInterface; use Symfony\Component\DependencyInjection\ContainerBuilder; class AddTemplatePluginDirCompilerPass implements CompilerPassInterface { public function process(ContainerBuilder $container) { $template = $container-&gt;getDefinition(&#39;template&#39;); $template-&gt;addMethodCall(&#39;addPluginsDir&#39;, [$container-&gt;getParameter(&#39;my_plugin.my_parameter_to_folder&#39;)]); } } {% endcode %}
{% code title=&ldquo;MyPlugin.php&rdquo; %}
&lt;?php class MyPlugin extends Plugin { public function build(ContainerBuilder $container) { parent::build($container); $container-&gt;addCompilerPass(new AddTemplatePluginDirCompilerPass()); } } {% endcode %}
